import React, { useEffect, useState } from "react";
import { useReduceState } from "../../context";
import { Link } from 'react-router-dom';
import $ from "jquery";

//Components
import Loading from "../../containers/DefaultLayout/DefaultLoading";

export default function DefaultHeaderDropdown(props) {
  const [genres, setgenres] = useState();
  if (!state.data || (state.data && !state.data.genre)) {
    getGenres("genre");
  }
  useEffect(() => {
    if(!genres){
      setgenres(state.data && state.data.genre);
      console.log(genres);
    }
    $("[data-toggle='collapse']").click(function () {
      var target = $(this).data("target");
      $(target).toggleClass("show");
      $(this).toggleClass("--opened");
    })
  })
  return genres ? (
    <div className="main-menu">
      <div className="navigation">
        <div className="menu-container">
          <ul id="navigation" className="menu-collpase">
            <div id="menu-button" data-toggle="collapse" data-target="#menuCollapse"></div>
            <ul id="menuCollapse" className="collapse">
              <li className="active">
                <Link to="/">Home</Link>
              </li>
              <li>
                <Link to="/about">About</Link>
              </li>
              <li>
                <Link to="/movies">Movies</Link>
              </li>
              <li className="has-sub"><span data-toggle="collapse" data-target="#pageCollapse" className="submenu-button"></span>
                <Link to="/">Page</Link>
                <ul id="pageCollapse" className="collapse">
                  <li><Link to="/Login">Login</Link></li>
                  <li><Link to="/Register">Register</Link></li>
                </ul>
              </li>
              <li className="has-sub"><span data-toggle="collapse" data-target="#blogCollapse" className="submenu-button"></span>
                <Link to="/blog">Blogs</Link>
                <ul id="blogCollapse" className="collapse">
                  <li className="has-sub"><span className="submenu-button"></span><Link to="/blog-details">Blog details</Link>
                  </li>
                </ul>
              </li>
              <li><Link to="/contact">Contact Us</Link></li>
            </ul>
          </ul>
        </div>
      </div>
    </div>
  ) : <Loading />;
}
