import React, { useEffect, useState } from "react";
import ReactPaginate from 'react-paginate';
// import { Link } from 'react-router-dom';
import { select } from 'reactstrap';
import { useReduceState } from "../../context";
import "./style.css";
import $ from "jquery";

//Components
import Breadcrumbs from "../../containers/DefaultLayout/DefaultBreadcrumbs";
import SideBar from "../../components/Sidebar";
import MovieList from "./MovieList";

var _ = require('lodash');
var reset = false;
export default function MoviePage(props) {
  const { state, firebase, getCollection } = useReduceState();
  const [type, setType] = useState();
  const [genres, setGenres] = useState();
  const [nation, setNation] = useState();
  const [year, setYear] = useState();
  const [params, setParams] = useState();
  const [keyword, setKeyword] = useState();
  const [filter, setFilter] = useState();
  const [dataPage, setDataPage] = useState();
  const [breadcrumb, setBreadcrumb] = useState();
  const [pages, setPages] = useState();
  const perPage = 4;

  const onFilter = event => {
    var ftype = document.getElementById("type").value;
    var fgenre = document.getElementById("genre").value;
    var fnation = document.getElementById("nation").value;
    var fyear = document.getElementById("year").value;

    if (ftype == 'all-type' && fgenre != 'all-genre') {
      props.history.push(`/the-loai/${fgenre}/${fnation}/${fyear}`)
    }
    else if (ftype == 'all-type' && fnation != 'all-nation') {
      props.history.push(`/quoc-gia/${fnation}/${fyear}`)
    }
    else if (ftype == 'all-type' && fyear != 'all-year') {
      props.history.push(`/phim-nam/${fyear}`)
    }
    else {
      props.history.push(`/loai/${ftype}/${fgenre}/${fnation}/${fyear}`)
    }
  };
  const onSort = event => {
    setFilter([]);
    var sort = event.target.value;
    var dataUpdated = filter.sort(function (a, b) {
      return b[sort] - a[sort];
    })
    setTimeout(() => {
      setFilter(dataUpdated);
    }, 100);
  };
  const onChangePage = data =>{
    let selected = data.selected;
    let offset = Math.ceil(selected * perPage);
    let dataUpdated = getDataPage(filter,offset);
    setDataPage(dataUpdated);
  }
  const getDataPage = (data,page) =>{
    let dataUpdated = [];
    for (let i = page;i < (page+perPage-1 < data.length ? page+perPage : data.length); i++) {
      dataUpdated.push(data[i]);
    }
    return dataUpdated;
  }
  if (!keyword && props.match.params.keyword) {
    reset = true;
    $(".zmoto-products").removeClass("fadeIn");
    setKeyword(props.match.params.keyword);
  }
  if (!params || !_.isEqual(params, props.match.params)) {
    reset = true;
    $(".zmoto-products").removeClass("fadeIn");
    setParams(props.match.params);
  }

  if (!state.data || (state.data && !state.data.year)) {
    getCollection("year", { limit: 5 });
  }
  useEffect(() => {
    if (!type) {
      setType(state.data && state.data.type);
    }
    if (!genres) {
      setGenres(state.data && state.data.genre);
    }
    if (!nation) {
      setNation(state.data && state.data.nation);
    }
    if (!year) {
      setYear(state.data && state.data.year);
    }
    if (type && genres && nation && year && params && reset) {
      var aCondition = []; var aBreadcrumb = [];
      if (params.type && params.type != 'all-type') {
        let obj = _.find(type, { id: params.type });
        if (obj) {
          aCondition.push(["type", obj])
          aBreadcrumb.push({ url: `/${obj.id}`, label: obj.name })
        }
      }
      if (params.genre && params.genre != 'all-genre') {
        let obj = _.find(genres, { id: params.genre });
        if (obj) {
          aCondition.push(["genre", obj])
          aBreadcrumb.push({ url: `/the-loai/${obj.id}`, label: obj.name })
        }
      }
      if (params.nation && params.nation != 'all-nation') {
        let obj = _.find(nation, { id: params.nation });
        if (obj) {
          aCondition.push(["nation", obj])
          aBreadcrumb.push({ url: `/quoc-gia/${obj.id}`, label: obj.name })
        }
      }
      if (params.year && params.year != 'all-year') {
        aCondition.push(["year", params.year])
        aBreadcrumb.push({ url: `/phim-nam/${params.year}`, label: `Phim ${params.year}` })
      }
      if (aCondition.length) {
        firebase.getDocWithMultiCondition("movie", { where: aCondition, orderBy: ["createdAt", "desc"], limit: 10 }, setFilter);
        reset = false;
      }
      setBreadcrumb(aBreadcrumb);
    }
    if (keyword && reset) {
      firebase.getDocWithKeyword("movie", {
        where: [
          [`keywords CONTAINS '${props.match.params.keyword.replace(/ /g, "%20")}'`],
          [`tags CONTAINS '${props.match.params.keyword.replace(/ /g, "%20")}'`]
        ]
      }, setFilter);
      reset = false;
    }
  })
  // console.log("RESET",reset)
  // console.log("filterf",filter)
  if (!reset && filter) {
    $(".main-page").addClass("fadeIn");
    $(".zmoto-products").addClass("fadeIn");
  }
  if(!pages && !dataPage && filter){
    const dataUpdated = getDataPage(filter,0);
    setDataPage(dataUpdated);
    setPages(filter.length/4);
  }
  return (
    <div id="MoviePage" className="main-page fadeOut">
      {/* <!-- breadcroumb-area --> */}
      {
        props.match.params.keyword ? <Breadcrumbs items={[`Từ khóa: ${props.match.params.keyword}`]} /> : <Breadcrumbs items={breadcrumb} />
      }
      {/* <!-- End breadcroumb-area --> */}
      <div className="zmoto-inner-page">
        <div className="zmovo-product-page">
          <div className="container">
            <div className="zmovo-product-items">
              <div className="zmovo-product-items-inner">
                <div className="row">
                  <div className="col-12">
                    <div className="zmovo-product-with-sedbar">
                      <div className="row">
                        {/* <!-- Right --> */}
                        <div className="col-lg-9">
                          {/* <!-- Product Filter --> */}
                          <div className="product-filter mb-30">
                            <div className="product-filter-inner">
                              <div className="product-filter-list">
                                <div className="short-by-show form-inline2">
                                  <div className="row">
                                    <div className="col-6 col-sm-4 col-md-3 col-lg-2">
                                      <div className="short-by">
                                        <select type="select" id="sort" onChange={onSort} className="selectpicker" data-live-search="true">
                                          <option value="" hidden>Sắp xếp</option>
                                          <option value="createdAt">Mới nhất</option>
                                          <option value="imdb">Top IMDb</option>
                                          <option value="view">Lượt xem</option>
                                          <option value="rating">Top đánh giá</option>
                                        </select>
                                      </div>
                                    </div>
                                    {
                                      type ? (
                                        <div className="col-6 col-sm-4 col-md-3 col-lg-2">
                                          <div className="pro-show">
                                            <select type="select" id="type" className="selectpicker" defaultValue={params && params.type}>
                                              <option value="all-type">--Loại--</option>
                                              {
                                                type.map(function (item, key) {
                                                  return <option key={key} value={item.id}>{item.name}</option>
                                                })
                                              }
                                            </select>
                                          </div>
                                        </div>
                                      ) : ""
                                    }
                                    {
                                      genres ? (
                                        <div className="col-6 col-sm-4 col-md-3 col-lg-2">
                                          <div className="pro-show">
                                            <select type="select" id="genre" className="selectpicker" defaultValue={params && params.genre ? params.genre : props.match.params.genre || 0} >
                                              <option value="all-genre">--Thể loại--</option>
                                              {
                                                genres.map(function (item, key) {
                                                  return <option key={key} value={item.id}>{item.name}</option>
                                                })
                                              }
                                            </select>
                                          </div>
                                        </div>
                                      ) : ""
                                    }
                                    {nation ? (
                                      <div className="col-6 col-sm-4 col-md-3 col-lg-2">
                                        <div className="pro-show">
                                          <select type="select" id="nation" className="selectpicker" defaultValue={params && params.nation ? params.nation : props.match.params.nation || 0} >
                                            <option value="all-nation">--Quốc gia--</option>
                                            {
                                              nation.map(function (item, key) {
                                                return <option key={key} value={item.id}>{item.name}</option>
                                              })
                                            }
                                          </select>
                                        </div>
                                      </div>
                                    ) : ""}
                                    {year ? (
                                      <div className="col-6 col-sm-4 col-md-3 col-lg-2">
                                        <div className="pro-show">
                                          <select type="select" id="year" className="selectpicker" defaultValue={params && params.year ? params.year : props.match.params.year} >
                                            <option value="all-year">--Năm--</option>
                                            {
                                              year.map(function (item, key) {
                                                return <option key={key} value={item.id}>{item.id}</option>
                                              })
                                            }
                                          </select>
                                        </div>
                                      </div>
                                    ) : ""}
                                    <div className="col-6 col-sm-4 col-md-3 col-lg-2">
                                      <div className="pro-show">
                                        <button className="btn btn-buttons btn-sm btn-fit" onClick={onFilter}>Lọc phim</button>
                                      </div>
                                    </div>
                                  </div>
                                </div>
                              </div>
                            </div>
                          </div>
                          {/* <!-- end Product Filter --> */}
                          <div className="zmoto-products fadeOut">
                            <MovieList data={dataPage} />
                          </div>
                          {/* <!-- Product Filter --> */}
                          <div className="product-filter">
                            <div className="product-filter-inner">
                              <div className="product-filter-list">
                                <div className="pagination-wrapper">
                                  <nav aria-label="navigations">
                                    <ReactPaginate
                                      previousLabel={'<'}
                                      nextLabel={'>'}
                                      previousClassName={'page-item'}
                                      nextClassName={'page-item'}
                                      previousLinkClassName={'page-link pagefirst'}
                                      nextLinkClassName={'page-link lastpage'}
                                      breakLabel={'...'}
                                      breakClassName={'page-item break-me'}
                                      containerClassName={'pagination'}
                                      pageClassName={'page-item'}
                                      pageLinkClassName={'page-link'}
                                      activeClassName={'active'}
                                      pageCount={pages}
                                      marginPagesDisplayed={2}
                                      pageRangeDisplayed={5}
                                      onPageChange={onChangePage}
                                    />
                                  </nav>
                                </div>
                              </div>
                            </div>
                          </div>
                          {/* <!-- end Product Filter --> */}
                        </div>
                        {/* <!-- End Right --> */}
                        {/* <!-- Left --> */}
                        <div className="col-lg-3">
                          <SideBar />
                        </div>
                        {/* <!-- End Left --> */}
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
