import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { useReduceState } from "../../../context";
import "./style.css";
import $ from "jquery";

export default function LoginPage(props) {
    const { firebase } = useReduceState();
    const [account, setAccount] = useState();
    // const [password,setPassword] = useState();

    const handleUsernameChange = event => {
        var username = event.target.value;
        var validate = true;
        if (username.length < 6) {
            $("#tip-username").text("Từ 6 kí tự trở lên");
            validate = false;
        }
        else {
            $("#tip-username").text("");
            setAccount({ ...account, username, validate })
        }
    }
    const handlePasswordChange = event => {
        var password = event.target.value;
        var validate = true;
        if (password.length < 6) {
            $("#tip-password").text("Từ 6 kí tự trở lên");
            validate = false;
        }
        else {
            $("#tip-password").text("");
            setAccount({ ...account,password, validate })
        }
    }
    const onSubmitLogin = event => {
        event.preventDefault();
        if(!account.validate){

            return;
        }else {
            firebase.getDocWithMultiCondition("account", { where: [["username", account.username], ["password", account.password]] },
                (res) => {
                    if (res.length > 0) {
                        alert("Đăng nhập thành công !!!");
                    } else {
                        setAccount({ ...account, validate: false })
                        alert("Sai username hoặc password!");
                    }
                }
            );
        }
    }
    useEffect(() => {

    })
    return (
        <div className="zmovo-inner-page">
            <div className="zmovo-login-page">
                <div className="container">
                    <div className="zmovo-login-page-wrap">
                        <div className="zmovo-page-login">
                            <form onSubmit={onSubmitLogin}>
                                <div className="zmovo-login-input-top">
                                    <div className="form-group">
                                        <p className="tip-username"></p>
                                        <input name="username" onChange={handleUsernameChange} className="form-control" required="" type="text" placeholder="Tên đăng nhập" />
                                    </div>
                                    <div className="form-group">
                                        <p className="tip-password"></p>
                                        <input name="password" onChange={handlePasswordChange} className="form-control" required="" type="password" placeholder="Mật khẩu" />
                                    </div>
                                    <p className="tip-account"></p>
                                </div>
                                <div className="zmovo-remember-me">
                                    <input type="checkbox" id="remember" className="checkbox" />
                                    <label htmlFor="remember">Ghi nhớ đăng nhập</label>
                                </div>
                                <div className="zmovo-login-fb">
                                    <ul>
                                        <li className="log-fb"><a href="/"><span className="fa fa-facebook"></span>Facebook</a></li>
                                        <li className="log-goo"><a href="/"><span className="fa fa-google"></span>Google</a></li>
                                    </ul>
                                </div>
                                <div className="zmovo-login-btns">
                                    <button className="zmovo-login-btn">ĐĂNG NHẬP</button>
                                </div>
                                <div className="zmovo-forgate">
                                    <div className="zmovo-register-me">
                                        <Link to="/register">ĐĂNG KÝ</Link>
                                    </div>
                                    <div className="zmovo-forgate-me">
                                        <a href="/">Quên mật khẩu  ?</a>
                                    </div>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}
