import app from "firebase/app";
import "firebase/auth";
import "firebase/firebase-firestore";
// import { toast } from "react-toastify";
import Config from "../config";
class Firebase {
  constructor() {
    app.initializeApp(Config._env.firebase);
    this.auth = app.auth();
    this.db = app.firestore();
    this.authGoogle = new app.auth.GoogleAuthProvider();
    this.authGoogle.setCustomParameters({
      prompt: "select_account"
    });
    this.authFacebook = new app.auth.FacebookAuthProvider();
    this.authFacebook.setCustomParameters({
      prompt: "select_account"
    });
  }
  isInitialized() {
    return new Promise(resolve => {
      this.auth.onAuthStateChanged(resolve);
    });
  }
  getCurrentUsername() {
    return this.auth.currentUser;
  }
  getUserLogIn() {
    return this.db.doc("account/" + this.getUid()).get();
  }
  getUid = user => {
    !user && (user = this.auth.currentUser);
    return user && (user.email || (user.providerData[0] && user.providerData[0].uid) || user.uid);
  };
  checkSession = user => {
    !user && (user = this.auth.currentUser);
    // console.log("user.metadata.b", user && user.metadata.b);
    return user && new Date().getTime() - user.metadata.b < 86400000; //1 hour
  };

  logout() {
    return this.auth.signOut();
  }
  //------------------------------------------------------------------------//
  getDataWithCondition(c, p) {
    return p ?
      this.db
      .collection(c)
      .where(p.where[0], p.where[1], p.where[2])
      .orderBy(p.order ? p.order[0] : "createdAt", p.order ? p.order[1] : "desc")
      .limit(p.limit ? p.limit : 1000)
      .get(p.get && p.get) :
      this.db.collection(c).get();
  }
  getData(c, p) {
    return p ?
      this.db
      .collection(c)
      .orderBy(p.order ? p.order[0] : "createdAt", p.order ? p.order[1] : "desc")
      .limit(p.limit ? p.limit : 1000)
      .get(p.get && p.get) :
      this.db.collection(c).get();
  }
  getMovieItem(id) {
    return this.db
      .collection("movie")
      .doc(id)
      .get();
  }
  setMovieItem(id, data) {
    return this.db
      .collection("movie")
      .doc(id)
      .set(data);
  }

  // ----------
getDocWithCondition = (collection, params, key, callback) => {
  try {
    const api = this.db.collection(collection);
    params.where && params.where.map(function(item){
      api.where(item.doc,item.operator,item.value);
    })
    api.orderBy(params.order ? params.order[0] : "createdAt", params.order ? params.order[1] : "desc")
      .limit(params.limit ? params.limit : 1000);
    api.onSnapshot(function (snapshot) {
      const data = [];
      snapshot.docs.forEach(doc => {
        data.push({
          ...doc.data(),
          id: doc.id
        });
      });
      callback && callback(data);
      // var obj = {};
      // obj[key || collection] = data;
      // console.log("obj", obj);
      // callback && callback(obj);
    });
    return api.get();
  } catch (error) {
    console.error(error);
  }
};
}

export default new Firebase();
