import React, { createContext, useContext, useReducer } from "react";
import { initialState, mainReducer } from "./reducers";
const StateContext = createContext();
export const StateProvider = props => {
  const [state, dispatch] = useReducer(mainReducer, initialState);

  const getMovieById = (id, cb) => {
    firebase.getMovieItem(id).then(rs => {
      dispatch({ type: "GET_MOVIE_ITEM", data: { item: { ...rs.data(), id: rs.id } } });
      cb();
    });
  };
  const getMovies = (key, params) => {
    var ListMovie = []; var obj = {}
    firebase.getMovies(
      {
        "order": params.order ? params.order : ["createdAt", "desc"],
        "limit": params.limit ? params.limit : 1000
      }
    ).then(function (querySnapshot) {
      querySnapshot.forEach(function (doc) {
        ListMovie.push({ ...doc.data(), id: doc.id })
      });
      obj[key] = ListMovie;
      dispatch({ type: "GET_MOVIES", data: { ...obj } });
    });
  };
  const setMovieItem = (id, data) => {
    firebase.setMovieItem(id, data);
  };
  const value = {
    state,
    dispatch,
    firebase,
    getMovieById,
    getMovies,
    setMovieItem
  };

  return <StateContext.Provider value={value}>{props.children}</StateContext.Provider>;
};
export const useReduceState = () => useContext(StateContext);

export const firebase = require("./firebase").default;
