import React, { useEffect, useState } from "react";
// import { Link } from "react-router-dom";
import { useReduceState } from "../../context";
import "./style.css";

// import data from "../../api/data.json"; 

// import $ from "jquery";
import OwlCarousel from 'react-owl-carousel2';
import 'react-owl-carousel2/src/owl.carousel.css';

//Components
import Loading from "../../containers/DefaultLayout/DefaultLoading";
import Slider from "../../components/Slider";
import VideoList from "../../components/VideoList";
import VideoItem from "../../components/VideoItem";
import FullBanner from "../../components/FullBanner";

// Constant
const opBox = {
  items: 5,
  margin: 30,
  nav: true,
  navText: ['<i class="fa fa-angle-left"></i>', '<i class="fa fa-angle-right"></i>'],
  dots: false,
  rewind: false,
  autoplay: false,
  loop: true,
  responsive: {
    0: {
      items: 2,
    },
    480: {
      items: 3
    },
    768: {
      items: 4
    },
    1024: {
      items: 5
    }
  }
};
export default function HomePage(props) {
  const { state, getDocWithCondition,setMovieItem } = useReduceState();
  const [theater, settheater] = useState();
  const [feature, setfeature] = useState();
  const [series, setseries] = useState();
  const [anime, setanime] = useState();

  if (!state.data || (state.data && !state.data.theater)) {
    getDocWithCondition("movie", {limit: 10 }, "theater");
  }
  if (!state.data || (state.data && !state.data.feature)) {
    getDocWithCondition("movie", {limit: 10 }, "feature");
  }
  if (!state.data || (state.data && !state.data.series)) {
    getDocWithCondition("movie", {limit: 10 }, "series");
  }
  if (!state.data || (state.data && !state.data.anime)) {
    getDocWithCondition("movie", {limit: 10 }, "anime");
  }
  if (!state.data || (state.data && !state.data.category)) {
    // getListByField("category");
  }
  useEffect(() => {
    if (state.data && state.data.theater && !theater) {
      console.log("1",state.data);
      settheater(state.data && state.data.theater);
    }
    if (state.data && state.data.feature && !feature) {
      console.log("2",state.data);
      setfeature(state.data && state.data.feature);
    }
    if (state.data && state.data.series && !series) {
      console.log("3",state.data);
      setseries(state.data && state.data.series);
    }
    if (state.data && state.data.anime && !anime) {
      console.log("4",state.data);
      setanime(state.data && state.data.anime);
    }    
    // setcategories(state.data && state.data.category);
    // setMovieItem("carnival-row",data[0])
  });
  return theater && feature && series && anime? (
    <div id="HomePage">
      {/*<!-- SLIDER SECTION --->*/}
      <Slider />
      {/*<!-- END SLIDER SECTION --->*/}
      {/* <!-- NEW ARRIVALS --> */}
      {theater.length > 0 ? (<div className="zmovo-new-arrivals arow-icon">
        <div className="container">
          <div className="zmovo-arrivals-items">
            <div className="zmovo-hadidng pt-50 pb-30">
              <h2><span className="text-uppercase">Phim chiếu rạp mới</span></h2>
            </div>
            {/* <!-- items --> */}
            <div className="items" id="new-arrivle">
              <OwlCarousel options={opBox}>
                {/* <!-- ITEM --> */}
                {theater.map(function (item, key) {
                  return (
                    <div className="item" key={key}>
                      <VideoItem data={item} />
                    </div>
                  )
                })}
              </OwlCarousel>
            </div>
            {/* <!-- end items --> */}
            {/* <!-- ALL VIEW --> */}
            <div className="button pt-50 text-center">
              <a href="/" className="btn btn-radus">view all <span className="fa fa-angle-right"></span></a>
            </div>
            {/* <!-- END ALL VIEW --> */}
          </div>
        </div>
      </div>):""}
      {/* <!-- END NEW ARRIVALS --> */}
      {/* <!-- NEW ARRIVALS --> */}
      {feature.length > 0 ? (<div className="zmovo-new-arrivals arow-icon">
        <div className="container">
          <div className="zmovo-arrivals-items">
            <div className="zmovo-hadidng pt-50 pb-30">
              <h2><span className="text-uppercase">Phim lẻ mới</span></h2>
            </div>
            {/* <!-- items --> */}
            <div className="items" id="new-arrivle">
              <OwlCarousel options={opBox}>
                {/* <!-- ITEM --> */}
                {feature.map(function (item, key) {
                  return (
                    <div className="item" key={key}>
                      <VideoItem data={item} />
                    </div>
                  )
                })}
              </OwlCarousel>
            </div>
            {/* <!-- end items --> */}
            {/* <!-- ALL VIEW --> */}
            <div className="button pt-50 text-center">
              <a href="/" className="btn btn-radus">view all <span className="fa fa-angle-right"></span></a>
            </div>
            {/* <!-- END ALL VIEW --> */}
          </div>
        </div>
      </div>):""}
      {/* <!-- END NEW ARRIVALS --> */}
      {/* <!-- NEW ARRIVALS --> */}
      {series.length > 0 ? (<div className="zmovo-new-arrivals arow-icon">
        <div className="container">
          <div className="zmovo-arrivals-items">
            <div className="zmovo-hadidng pt-50 pb-30">
              <h2><span className="text-uppercase">Phim bộ mới</span></h2>
            </div>
            {/* <!-- items --> */}
            <div className="items" id="new-arrivle">
              <OwlCarousel options={opBox}>
                {/* <!-- ITEM --> */}
                {series.map(function (item, key) {
                  return (
                    <div className="item" key={key}>
                      <VideoItem data={item} />
                    </div>
                  )
                })}
              </OwlCarousel>
            </div>
            {/* <!-- end items --> */}
            {/* <!-- ALL VIEW --> */}
            <div className="button pt-50 text-center">
              <a href="/" className="btn btn-radus">view all <span className="fa fa-angle-right"></span></a>
            </div>
            {/* <!-- END ALL VIEW --> */}
          </div>
        </div>
      </div>) : ""}
      {/* <!-- END NEW ARRIVALS --> */}
      {/* <!-- NEW ARRIVALS --> */}
      {anime.length > 0 ? (<div className="zmovo-new-arrivals arow-icon">
        <div className="container">
          <div className="zmovo-arrivals-items">
            <div className="zmovo-hadidng pt-50 pb-30">
              <h2><span className="text-uppercase">Anime mới cập nhật</span></h2>
            </div>
            {/* <!-- items --> */}
            <div className="items" id="new-arrivle">
              <OwlCarousel options={opBox}>
                {/* <!-- ITEM --> */}
                {anime.map(function (item, key) {
                  return (
                    <div className="item" key={key}>
                      <VideoItem data={item} />
                    </div>
                  )
                })}
              </OwlCarousel>
            </div>
            {/* <!-- end items --> */}
            {/* <!-- ALL VIEW --> */}
            <div className="button pt-50 text-center">
              <a href="/" className="btn btn-radus">view all <span className="fa fa-angle-right"></span></a>
            </div>
            {/* <!-- END ALL VIEW --> */}
          </div>
