import React, { useEffect, useState } from "react";
// import { Link } from "react-router-dom";
import { useReduceState } from "../../context";
import "./style.css";

// import data from "../../api/data.json";

import $ from "jquery";
import OwlCarousel from "react-owl-carousel2";
import "react-owl-carousel2/src/owl.carousel.css";

//Components
// import Loading from "../../containers/DefaultLayout/DefaultLoading";
import Slider from "../../components/Slider";
import VideoList from "../../components/VideoList";
import VideoItem from "../../components/VideoItem";
import FullBanner from "../../components/FullBanner";

// Constant
const opBox = {
  items: 5,
  margin: 30,
  nav: true,
  navText: ['<i class="fa fa-angle-left"></i>', '<i class="fa fa-angle-right"></i>'],
  dots: false,
  rewind: false,
  autoplay: true,
  autoplayTimeout: 2000,
  loop: true,
  responsive: {
    0: {
      items: 2
    },
    480: {
      items: 3
    },
    768: {
      items: 4
    },
    1024: {
      items: 5
    }
  }
};
var _ = require('lodash');
export default function HomePage(props) {
  // setMovieItem
  const { state, firebase } = useReduceState();
  const [type, setType] = useState();
  const [genres, setGenres] = useState();
  const [theater, settheater] = useState();
  const [feature, setfeature] = useState();
  const [series, setseries] = useState();
  const [anime, setanime] = useState();

  useEffect(() => {
    console.log(state.data)
    if (!type) {
      setType(state.data && state.data.type);
    }
    // if (!genres) {
    //   setGenres(state.data && state.data.genre);
    // }
    if (type) {
      console.log(type)
      if(!theater){
        let obj = _.find(type, { id: "phim-chieu-rap" });
        firebase.getDocWithCondition(
          "movie",                                  
          { where: ["type",obj], limit: 10 }, 
          "theater", 
          settheater);
      }
      if(!feature){
        let obj = _.find(type, { id: "phim-le" });
        firebase.getDocWithCondition(
          "movie",
          { where: ["type",obj], limit: 10 },
          "feature",
          setfeature
        );
      }
      if(!series){
        let obj = _.find(type, { id: "phim-bo" });
        firebase.getDocWithCondition(
          "movie",
          { where: ["type",obj], limit: 10 },
          "series",
          setseries
        );
      }
    }
    if(genres){
      let obj = _.find(genres, { id: "hoat-hinh" });
      firebase.getDocWithCondition(
        "movie",
        { where: ["genre", obj], limit: 10 },
        "anime",
        setanime
      );
    }

  });
  if (theater && feature && series && anime) {
    $(".main-page").addClass("fadeIn");
  }
  return (
    <div id="HomePage" className="main-page fadeOut">
      {/*<!-- SLIDER SECTION --->*/}
      <Slider />
      {/*<!-- END SLIDER SECTION --->*/}
      {/* <!-- NEW ARRIVALS --> */}
      {theater && theater.length > 0 ? (
        <div className="zmovo-new-arrivals arow-icon">
          <div className="container">
            <div className="zmovo-arrivals-items">
              <div className="zmovo-hadidng pt-50 pb-30">
                <h2>
                  <span className="text-uppercase">Phim chiếu rạp mới</span>
                </h2>
              </div>
              {/* <!-- items --> */}
              <div className="items" id="new-arrivle">
                <OwlCarousel options={opBox}>
                  {/* <!-- ITEM --> */}
                  {theater.map(function (item, key) {
                    return (
                      <div className="item" key={key}>
                        <VideoItem data={item} />
                      </div>
                    );
                  })}
                </OwlCarousel>
              </div>
              {/* <!-- end items --> */}
              {/* <!-- ALL VIEW --> */}
              <div className="button pt-50 text-center">
                <a href="/" className="btn btn-radus">
                  view all <span className="fa fa-angle-right"></span>
                </a>
              </div>
              {/* <!-- END ALL VIEW --> */}
            </div>
          </div>
        </div>
      ) : (
          ""
        )}
      {/* <!-- END NEW ARRIVALS --> */}
      {/* <!-- NEW ARRIVALS --> */}
      {feature && feature.length > 0 ? (
        <div className="zmovo-new-arrivals arow-icon">
          <div className="container">
            <div className="zmovo-arrivals-items">
              <div className="zmovo-hadidng pt-50 pb-30">
                <h2>
                  <span className="text-uppercase">Phim lẻ mới</span>
                </h2>
              </div>
              {/* <!-- items --> */}
              <div className="items" id="new-arrivle">
                <OwlCarousel options={opBox}>
                  {/* <!-- ITEM --> */}
                  {feature.map(function (item, key) {
                    return (
                      <div className="item" key={key}>
                        <VideoItem data={item} />
                      </div>
                    );
                  })}
                </OwlCarousel>
              </div>
              {/* <!-- end items --> */}
              {/* <!-- ALL VIEW --> */}
              <div className="button pt-50 text-center">
                <a href="/" className="btn btn-radus">
                  view all <span className="fa fa-angle-right"></span>
                </a>
              </div>
              {/* <!-- END ALL VIEW --> */}
            </div>
          </div>
        </div>
      ) : (
          ""
        )}
      {/* <!-- END NEW ARRIVALS --> */}
      {/* <!-- NEW ARRIVALS --> */}
      {series && series.length > 0 ? (
        <div className="zmovo-new-arrivals arow-icon">
          <div className="container">
            <div className="zmovo-arrivals-items">
              <div className="zmovo-hadidng pt-50 pb-30">
                <h2>
                  <span className="text-uppercase">Phim bộ mới</span>
                </h2>
              </div>
              {/* <!-- items --> */}
              <div className="items" id="new-arrivle">
                <OwlCarousel options={opBox}>
                  {/* <!-- ITEM --> */}
                  {series.map(function (item, key) {
                    return (
                      <div className="item" key={key}>
                        <VideoItem data={item} />
                      </div>
                    );
                  })}
                </OwlCarousel>
              </div>
              {/* <!-- end items --> */}
              {/* <!-- ALL VIEW --> */}
              <div className="button pt-50 text-center">
                <a href="/" className="btn btn-radus">
                  view all <span className="fa fa-angle-right"></span>
                </a>
              </div>
              {/* <!-- END ALL VIEW --> */}
            </div>
          </div>
        </div>
      ) : (
          ""
        )}
      {/* <!-- END NEW ARRIVALS --> */}
      {/* <!-- NEW ARRIVALS --> */}
      {anime && anime.length > 0 ? (
        <div className="zmovo-new-arrivals arow-icon">
          <div className="container">
            <div className="zmovo-arrivals-items">
              <div className="zmovo-hadidng pt-50 pb-30">
                <h2>
                  <span className="text-uppercase">Anime mới cập nhật</span>
                </h2>
              </div>
              {/* <!-- items --> */}
              <div className="items" id="new-arrivle">
