import React, { useEffect } from "react";
// import { Link } from 'react-router-dom';
import $ from "jquery";
import "./style.css";

export default function SideBar(props) {
    const { firebase } = useReduceState();
    const [trailers, setTrailers] = useState();
    useEffect(() => {
        if (!trailers) {
            firebase.getDocWithCondition("movie", { where: ["status", 0], orderBy: ["view", "desc"] }, setTrailers);
        }
    }, [trailers, firebase])
    return (
        <div className="zmovo-sidebar">
            <div className="zmovo-widget">
                <div className="zmovo-video-list-inner">
                    <h2 className="widget-title mb-30 mt-30"><span>Top trailer</span></h2>
                    <div className="zmovo-video-list-items">
                        <div className="zmovo-v-list-items mt-30">
                            {trailers && trailers.map(function (item, key) {
                                return (
                                    <div key={key} className="zmovo-v-list-item mb-30">
                                        <div className="zmovo-v-list-i-img">
                                            <img src={"./assets/images/movie/" + props.data.id + "/" + props.data.poster} alt="poster" />
                                        </div>
                                        <div className="zmovo-v-list-content">
                                            <div className="zmovo-v-tag c2"><span>English,2019</span></div>
                                            <a href="/">Failure of Four and Probab the</a>
                                            <div className="movie-time"><i className="fa fa-clock-o c1"></i><span>2 Hr 3 Min</span></div>
                                        </div>
                                    </div>
                                )
                            })}
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}
