import {
  FireSQL
} from 'firesql';
import app from "firebase/app";
import "firebase/auth";
import "firebase/firebase-firestore";
// import { toast } from "react-toastify";
import Config from "../config";
class Firebase {
  constructor() {
    app.initializeApp(Config._env.firebase);
    this.auth = app.auth();
    this.db = app.firestore();
    this.fireSQL = new FireSQL(this.db);
    this.authGoogle = new app.auth.GoogleAuthProvider();
    this.authGoogle.setCustomParameters({
      prompt: "select_account"
    });
    this.authFacebook = new app.auth.FacebookAuthProvider();
    this.authFacebook.setCustomParameters({
      prompt: "select_account"
    });
  }
  isInitialized() {
    return new Promise(resolve => {
      this.auth.onAuthStateChanged(resolve);
    });
  }
  getCurrentUsername() {
    return this.auth.currentUser;
  }
  getUserLogIn() {
    return this.db.doc("account/" + this.getUid()).get();
  }
  getUid = user => {
    !user && (user = this.auth.currentUser);
    return user && (user.email || (user.providerData[0] && user.providerData[0].uid) || user.uid);
  };
  checkSession = user => {
    !user && (user = this.auth.currentUser);
    // console.log("user.metadata.b", user && user.metadata.b);
    return user && new Date().getTime() - user.metadata.b < 86400000; //1 hour
  };

  logout() {
    return this.auth.signOut();
  }
  //------------------------------------------------------------------------//
  getDataWithCondition(c, p) {
    return p ?
      this.db
      .collection(c)
      .where(p.where[0], p.where[1], p.where[2])
      .orderBy(p.order ? p.order[0] : "createdAt", p.order ? p.order[1] : "desc")
      .limit(p.limit ? p.limit : 1000)
      .get(p.get && p.get) :
      this.db.collection(c).get();
  }
  getData(c, p) {
    return p ?
      this.db
      .collection(c)
      .orderBy(p.order ? p.order[0] : "createdAt", p.order ? p.order[1] : "desc")
      .limit(p.limit ? p.limit : 1000)
      .get(p.get && p.get) :
      this.db.collection(c).get();
  }
  getMovieItem(id) {
    return this.db
      .collection("movie")
      .doc(id)
      .get();
  }
  setMovieItem(id, data) {
    return this.db
      .collection("movie")
      .doc(id)
      .set(data);
  }

  // ----------
  getDocWithCondition = (collection, params, callback) => {
    try {
      var api;
      var where = "";
      if (typeof params.where[1] === "object") {
        where += ".where('" + params.where[0] + "." + params.where[1].id + "','==','" + params.where[1].name + "')";
      } else {
        where += ".where('" + params.where[0] + "','==','" + params.where[1] + "')";
      }
      let query = `this.db.collection('${collection}')${where}`;
      // console.log(query);
      api = eval(query);
      api.onSnapshot(function (snapshot) {
        const data = [];
        snapshot.docs.forEach(doc => {
          data.push({
            ...doc.data(),
            id: doc.id
          });
        });
        // console.log("data", data);
        callback && callback(data);
      });
      // return api.get();
    } catch (error) {
      console.error(error);
    }
  };
  getDocWithMultiCondition = (collection, params, callback) => {
    try {
      var api;
      var where = "";
      params.where && params.where.map(function (item) {
        // console.log(item)
        if (typeof item[1] === "object") {
          where += ".where('" + item[0] + "." + item[1].id + "','==','" + item[1].name + "')";
        } else {
          where += ".where('" + item[0] + "','==','" + item[1] + "')";
        }
      })
      let query = `this.db.collection(collection)${where}`;
      // console.log(query);
      api = eval(query);
      api.onSnapshot(function (snapshot) {
        const data = [];
        snapshot.docs.forEach(doc => {
          data.push({
            ...doc.data(),
            id: doc.id
          });
        });
        if(params.orderBy){
          data.sort(function(a, b){
            return (params.orderBy[1] === "asc"?1:-1)*(a[params.orderBy[0]] - b[params.orderBy[0]])
          })
        }else{
          data.sort(function(a, b){
            return b.createdAt - a.createdAt;
          })
        }
        // console.log("data", data);
        callback && callback(data);
      });
      // return api.get();
    } catch (error) {
      console.error(error);
    }
  };
  getDocWithMultiConditionSQL = (collection, params,callback) => {
    try {
      var where = "";
      if (params.where) {
        where = " WHERE ";
        params.where.map(function (item, idx) {
          // console.log(idx)
          if (typeof item[1] === "object") {
            let item
            item[2].map(function (item, idx) {
            })
            where += (idx === 0 ? "" : " AND ") + "`" + item[0] + "` IN '" + item[1].id + "'";
          } else {
            where += (idx === 0 ? "" : " AND ") + "`" + item[0] + "` = '" + item[1] + "'";
          }
        })
      }

      let query = 'SELECT * FROM ' + collection +
        where +
        ' ORDER BY createdAt DESC' +
        ' LIMIT 2';
      console.log("QUERY", query);
      const api = this.fireSQL.query(query);
      api.then(data => {
        console.log("data", data);
        callback && callback(data);
      });
      // return api.get();
    } catch (error) {
      console.error(error);
    }
  };
  getDocWithKeyword = (collection, params, callback) => {
    try {
      let query = `SELECT * FROM ${collection} WHERE ${params.where[0]} OR ${params.where[1]}`;
      console.log("QUERY", query);
      const api = this.fireSQL.query(query);
      api.then(data => {
        if(params.orderBy){
          data.sort(function(a, b){
            return (params.orderBy[1] === "asc"?1:-1)*(a[params.orderBy[0]] - b[params.orderBy[0]])
          })
        }else{
          data.sort(function(a, b){
            return b.createdAt - a.createdAt;
          })
        }
        console.log("data", data);
        callback && callback(data);
      });
      // return api.get();
    } catch (error) {
      console.error(error);
    }
  };
}

export default new Firebase();
