import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { useReduceState } from "../../context";
import "./style.css";
import $ from "jquery";
import data from "../../api/data.json";

// Components
import DefaultBreadcrumbs from "../../containers/DefaultLayout/DefaultBreadcrumbs";
import SideBar from "../../components/Sidebar";
import Comments from "../../components/Comments";
import MovieItemContent from "../../components/MovieItemContent";

var _ = require("lodash");

export default function MovieDetailsPage(props) {
  const [item, setItem] = useState(_.find(data, { id: props.match.params.id }));
  const { state, firebase, getMovieById } = useReduceState();
  const [movieItem, setMovieItem] = useState();
  if (!state.movie) {
    getMovieById("l9oi3R5IvVmIMaSdZy8H");
  }
  console.log("movieItem", movieItem);
  console.log("state", state.movie);
  useEffect(() => {
    // firebase.getMoveItem("l9oi3R5IvVmIMaSdZy8H").then(rs => {
    //   console.log("rs", rs.data());
    //   setMovieItem(rs.data());
    // });
    $("#openTrailer").click(function() {
      $(".zmovo-trailer-popup").show();
      var symbol = $(".zmovo-trailer-popup iframe")[0].src.indexOf("?") > -1 ? "&" : "?";
      $(".zmovo-trailer-popup iframe")[0].src += symbol + "autoplay=1";
    });
    $("#closeTrailer").click(function() {
      $(".zmovo-trailer-popup").hide();
      var symbol = $(".zmovo-trailer-popup iframe")[0].src.indexOf("?") > -1 ? "&" : "?";
      $(".zmovo-trailer-popup iframe")[0].src += symbol + "autoplay=1";
    });
  });
  //const item = _.find(data, {'id': props.match.params.id});
  return (
    <div id="MovieDetailsPage">
      {/* <!-- breadcroumb-area --> */}
      <DefaultBreadcrumbs items={[item.name]} />
      {/* <!-- End breadcroumb-area --> */}
      <div className="zmoto-inner-page">
        <div className="zmovo-blog-details pt-30">
          <div className="container">
            <div className="zmovo-blog-contents">
              <div className="row">
                <div className="col-12">
                  <div className="zmovo-product-with-sedbar">
                    <div className="row">
                      {/* <!-- Left --> */}
                      <div className="col-lg-9">
                        <MovieItemContent movie={item} />
                      </div>
                      {/* <!-- End Left --> */}
                      {/* <!-- Right --> */}
                      <div className="col-lg-3">
                        <SideBar />
                      </div>
                      {/* <!-- End Right --> */}
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
