import React, {
  useEffect,
  useState
} from "react";
import {
  Link
} from "react-router-dom";
import {
  useReduceState
} from "../../context";
import "./style.css";
import data from "../../api/data.json";

// Components
import DefaultBreadcrumbs from "../../containers/DefaultLayout/DefaultBreadcrumbs";
import SideBar from "../../components/Sidebar";
import Comments from "../../components/Comments";
import TrailerPopup from "./TrailerPopup";
import MovieDecs from "./MovieDecs";
import MovieStory from "./MovieStory";
import SocialLink from "../../components/SocialLink";

var _ = require("lodash");

export default function MovieDetailsPage(props) {
  const [item, setItem] = useState(_.find(data, {
    nameId: props.match.params.id
  }));
  const {
    state,
    firebase,
    getMovieById
  } = useReduceState();
  const [movieItem, setMovieItem] = useState();

  useEffect(() => {
    getMovieById("tFbfxDGDVoUJuELPjtfc");
    console.log("state", state);
  }, []);
  //const item = _.find(data, {'id': props.match.params.id});
  return ( <
    div id = "MovieDetailsPage" > {
      /* <!-- breadcroumb-area --> */ } <
    DefaultBreadcrumbs items = {
      [item.name]
    }
    /> {
      /* <!-- End breadcroumb-area --> */ } <
    div className = "zmoto-inner-page" >
    <
    div className = "zmovo-blog-details pt-30" >
    <
    div className = "container" >
    <
    div className = "zmovo-blog-contents" >
    <
    div className = "row" >
    <
    div className = "col-12" >
    <
    div className = "zmovo-product-with-sedbar" >
    <
    div className = "row" > {
      /* <!-- Left --> */ } <
    div className = "col-lg-9" >
    <
    div className = "zmovo-blog-dec-contents" >
    <
    div className = "zmovo-slider-area slider3" >
    <
    div className = "items"
    id = "slider3" >
    <
    div className = "item" > {
      /* Trailer Popup */ } <
    TrailerPopup item = {
      item
    }
    /> <
    div className = "zmovo-slider-contents gradient"
    style = {
      {
        backgroundImage: "url(./assets/images/movie/" + item.id + "/" + item.banner + ")"
      }
    } >
    <
    div id = "openTrailer"
    className = "curpoint zmovo-slider-premium-tag" >
    <
    span className = "c2-bg" > Trailer < /span> <
    /div> <
    div className = "zmovo-slide-content" > {
      /* Movie Desc */ } <
    MovieDecs item = {
      item
    }
    /> <
    /div> <
    /div> <
    /div> <
    /div> <
    /div> <
    div className = "zmovo-blog-dec-content pt-30" >
    <
    MovieStory / >
    <
    SocialLink / >
    <
    /div> <
    Comments / >
    <
    /div> <
    /div> {
      /* <!-- End Left --> */ } {
      /* <!-- Right --> */ } <
    div className = "col-lg-3" >
    <
    SideBar / >
    <
    /div> {
      /* <!-- End Right --> */ } <
    /div> <
    /div> <
    /div> <
    /div> <
    /div> <
    /div> <
    /div> <
    /div> <
    /div>
  );
}
