import React from "react";
import { Link } from 'react-router-dom';

import "./style.css";
import OwlCarousel from 'react-owl-carousel2';
import 'react-owl-carousel2/src/owl.carousel.css';

class VideoItem extends React.Component {
    // constructor(props) {
    //     super(props);
    // }
    componentDidMount() {

    }
    render() {
        return (
            <div className="zmovo-video-item-box">
                <div className="zmovo-video-box-inner">
                    <Link to={"/product-details/" + item.id}>
                        <div className="zmovo-v-box-img gradient">

                            <img src={"./assets/images/product/" + item.poster} alt="" />
                            <div className="ply-btns">
                                <img src="./assets/images/play-button.png" alt="" />
                            </div>
                        </div>
                    </Link>
                    <div className="zmovo-v-box-content">
                        <a href={"/product-details/" + item.id}>{item.name}</a>
                        <div className="zmovo-v-tag c2">
                            <span>{item.subname}</span>
                        </div>
                        <div className="movie-time"><i className="fa fa-clock-o c1"></i><span>{item.time + " phút"}</span></div>
                        <div className="like-icon">
                            <a href="/"><span className="fa fa-heart-o"></span></a>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default VideoItem;
