import React, { useEffect, useState } from "react";
// import { Link } from 'react-router-dom';
import { useReduceState } from "../../context";
import "./style.css";
import $ from "jquery";

//Components
import Breadcrumbs from "../../containers/DefaultLayout/DefaultBreadcrumbs";
import SideBar from "../../components/Sidebar";
import VideoItem from "../../components/VideoItem";

var _ = require('lodash');
var reset = false;
export default function MoviePage(props) {
  const { state, firebase, getCollection } = useReduceState();
  const [type, setType] = useState();
  const [genres, setGenres] = useState();
  const [nation, setNation] = useState();
  const [year, setYear] = useState();
  const [params, setParams] = useState();
  const [filter, setFilter] = useState();
  const onChange = event => {
    props.history.push('/the-loai/'+event.target.value)
  };
  if (!params || !_.isEqual(params, props.match.params)) {
    reset = true; 
    $(".zmoto-products").removeClass("fadeIn");
    setParams(props.match.params);
  }
  if (!state.data || (state.data && !state.data.year)) {
    getCollection("year", { limit: 5 });
  }
  useEffect(() => {
    if (!genres) {
      setGenres(state.data && state.data.genre);
    }
    if (!nation) {
      setNation(state.data && state.data.nation);
    }
    if (!type) {
      setType(state.data && state.data.type);
    }
    if (!year) {
      setYear(state.data && state.data.year);
    }
    if (genres && params && reset) {
      firebase.getDocWithCondition("movie",{ where: ["genre", "array-contains", _.find(genres, { id: params.genre })], limit: 10 },"filter",setFilter);
      reset = false;
    }
  })
  if(!reset && filter){
    $("html").scrollTop(0);
    $(".main-page").addClass("fadeIn");
    $(".zmoto-products").addClass("fadeIn");
  }
  return (
    <div id="MoviePage" className="main-page fadeIn">
      {/* <!-- breadcroumb-area --> */}
      <Breadcrumbs items={["phim"]} />
      {/* <!-- End breadcroumb-area --> */}
      <div className="zmoto-inner-page">
        <div className="zmovo-product-page">
          <div className="container">
            <div className="zmovo-product-items">
              <div className="zmovo-product-items-inner">
                <div className="row">
                  <div className="col-12">
                    <div className="zmovo-product-with-sedbar">
                      <div className="row">
                        {/* <!-- Right --> */}
                        <div className="col-lg-9">
                          {/* <!-- Product Filter --> */}
                          <div className="product-filter mb-30">
                            <div className="product-filter-inner">
                              <div className="product-filter-list">
                                <div className="short-by-show form-inline2">
                                  <div className="row">
                                    <div className="col-6 col-sm-4 col-md-3 col-lg-2">
                                      <div className="short-by">
                                        <select className="selectpicker" data-live-search="true">
                                          <option hidden>Sắp xếp</option>
                                          <option value="">Name (A - Z)</option>
                                          <option value="">Name (Z - A)</option>
                                          <option value="">Price (Low &gt; High)</option>
                                          <option value="">Price (High &gt; Low)</option>
                                          <option value="">Rating (Highest)</option>
                                          <option value="">Rating (Lowest)</option>
                                          <option value="">Model (A - Z)</option>
                                          <option value="">Model (Z - A)</option>
                                        </select>
                                      </div>
                                    </div>
                                    <div className="col-6 col-sm-4 col-md-3 col-lg-2">
                                      <div className="pro-show">
                                        <select className="selectpicker" data-live-search="true">
                                          <option value="">--Loại--</option>
                                          {
                                            type && type.map(function (item, key) {
                                              return <option key={key} value={item.id}>{item.name}</option>
                                            })
                                          }
                                        </select>
                                      </div>
                                    </div>
                                    <div className="col-6 col-sm-4 col-md-3 col-lg-2">
                                      <div className="pro-show">
                                        <select className="selectpicker" onChange={onChange} data-live-search="true">
                                          <option value="">--Thể loại--</option>
                                          {
                                            genres && genres.map(function (item, key) {
                                              return <option key={key} value={item.id}>{item.name}</option>
                                            })
                                          }
                                        </select>
                                      </div>
                                    </div>
                                    <div className="col-6 col-sm-4 col-md-3 col-lg-2">
                                      <div className="pro-show">
                                        <select className="selectpicker" data-live-search="true">
                                          <option value="">--Năm--</option>
                                          {
                                            year && year.map(function (item, key) {
                                              return <option key={key} value={item.id}>{item.id}</option>
                                            })
                                          }
                                        </select>
                                      </div>
                                    </div>
                                    <div className="col-6 col-sm-4 col-md-3 col-lg-2">
                                      <div className="pro-show">
                                        <select className="selectpicker" data-live-search="true">
                                          <option value="">--Quốc gia--</option>
                                          {
                                            nation && nation.map(function (item, key) {
                                              return <option key={key} value={item.id}>{item.name}</option>
                                            })
                                          }
                                        </select>
                                      </div>
                                    </div>
                                    <div className="col-6 col-sm-4 col-md-3 col-lg-2">
                                      <div className="pro-show">
                                        <button className="btn btn-buttons btn-sm btn-fit">Lọc phim</button>
                                      </div>
                                    </div>
                                  </div>
                                </div>
                              </div>
                            </div>
                          </div>
                          {/* <!-- end Product Filter --> */}
                          <div className="zmoto-products fadeOut">
                            <div className="zmovo-product-list">
                              <div className="row">
                                {/* <!-- ITEM --> */}
                                {filter && filter.map(function (item, key) {
                                  return (
                                    <div key={key} className="col-lg-3 col-md-4 col-xs-6">
                                      <div className="item" >
                                        <VideoItem data={item} />
                                      </div>
                                    </div>
                                  )
                                })}
                              </div>
                            </div>
                          </div>
                          {/* <!-- Product Filter --> */}
                          <div className="product-filter">
                            <div className="product-filter-inner">
                              <div className="product-filter-list">
