import React, { createContext, useContext, useReducer } from "react";
import { initialState, mainReducer } from "./reducers";
const StateContext = createContext();
export const StateProvider = props => {
  const [state, dispatch] = useReducer(mainReducer, initialState);

  const getMovieById = id => {
    console.log("getMovieById");
    firebase.getMoveItem().onSnapshot({
      includeMetadataChanges: true
    }, function (snapshot) {
      snapshot.docChanges().forEach(function (change) {
        if (change.type === "added") {
          console.log("change.doc.data()",change.doc.data());

          dispatch({ type: "SET_MOVIE_ITEM", movie: change.doc.data() });
        }

        var source = snapshot.metadata.fromCache ? "local cache" : "server";
        console.log("Data came from " + source);
      });
    });
      // dispatch({ type: "SET_MOVIE_ITEM", movie: rs.data() });
  };
  const value = {
    state,
    dispatch,
    firebase,
    getMovieById
  };

  return <StateContext.Provider value={value}>{props.children}</StateContext.Provider>;
};
export const useReduceState = () => useContext(StateContext);

export const firebase = require("./firebase").default;
