import React, {
  createContext,
  useContext,
  useReducer
} from "react";
import {
  initialState,
  mainReducer
} from "./reducers";
const StateContext = createContext();
export const StateProvider = props => {
  const [state, dispatch] = useReducer(mainReducer, initialState);

  const getMovieById = (id) => {
    firebase.getMovieItem(id).then(rs => {
      dispatch({
        type: "GET_MOVIE_ITEM",
        movie: {
          ...rs.data(),
          id: rs.id
        }
      });
    });
  };
  const getNewMovies = () => {
    var ListMovie =  []
    firebase.getMovies(["createdAt", "asc"]).then(function (querySnapshot) {
      querySnapshot.forEach(function (doc) {
        ListMovie.push(doc.data())
      });
      console.log("ListMovie",ListMovie);
      dispatch({ type: "GET_MOVIES", movie: {list:ListMovie} });
    });
  };
  const value = {
    state,
    dispatch,
    firebase,
    getMovieById,
    getNewMovies
  };

  return <StateContext.Provider value = {
    value
  } > {
    props.children
  } < /StateContext.Provider>;
};
export const useReduceState = () => useContext(StateContext);

export const firebase = require("./firebase").default;
