import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { useReduceState } from "../../../context";
import "./style.css";
import $ from "jquery";


export default function RegisterPage(props) {
    const { firebase } = useReduceState();
    const [account, setAccount] = useState();

    const handleUsernameChange = event => {
        var username = event.target.value;
        var validate = true;
        if (username.length < 1) {
            $("#tip-username").text("Từ 6 kí tự trở lên").show();
            validate = false;
        }
        else {
            $("#tip-username").text("").hide();
        }
        setAccount({ ...account, username, validate })
    }
    return (
        <div className="zmovo-inner-page">
            <div className="zmovo-login-page">
                <div className="zmovo-login-page-wrap">
                    <div className="container">
                        <div className="zmovo-page-login">
                            <form action="#">
                                <div className="zmovo-login-input-top">
                                    <div className="form-group">
                                        <p className="tip" style={{ display: "none" }} id="tip-password"></p>
                                        <input className="form-control" onChange={handleUsernameChange} type="text" placeholder="Enter User Name" />
                                    </div>
                                    <div className="form-group">
                                        <p className="tip" style={{ display: "none" }} id="tip-password"></p>
                                        <input className="form-control" required="" type="text" placeholder="Enter your Email" />
                                    </div>
                                    <div className="form-group">
                                        <p className="tip" style={{ display: "none" }} id="tip-password"></p>
                                        <input className="form-control" required="" type="password" placeholder="Enter your Password" />
                                    </div>
                                    <div className="form-group">
                                        <p className="tip" style={{ display: "none" }} id="tip-password"></p>
                                        <input className="form-control" required="" type="password" placeholder="Enter Conform Password" />
                                    </div>
                                </div>
                                <div className="zmovo-login-btns">
                                    <a href="/" className="zmovo-login-btn">ĐĂNG KÝ</a>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );

}
