import movieReducer from "./movie";
export const mainReducer = ({ theme, movie, movies }, action) => ({
  movies: movieReducer(movies, action),
  movie: movieReducer(movie, action)
});
export const initialState = { theme: { primary: "green" },movies:undefinedundefined, movie: undefined };
